for (const item of items) {
  console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> ƒ∞≈ülenen item'ƒ±n yapƒ±sƒ± (d√∂ng√º ba≈üƒ±):", JSON.stringify(item, null, 2));

  // 3. item.json.data'nƒ±n var olup olmadƒ±ƒüƒ±nƒ± ve string olup olmadƒ±ƒüƒ±nƒ± kontrol et
  // Girdi verinize g√∂re, API yanƒ±tƒ± item.json.data i√ßinde bir string olarak geliyor.
  const jsonString = item.json && item.json.data;

