      if (flightOffers && Array.isArray(flightOffers) && flightOffers.length > 0) {
        console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> flightOffers ko≈üulu saƒülandƒ±, i√ß d√∂ng√ºye giriliyor. Teklif sayƒ±sƒ±:", flightOffers.length);

        // 6. Her bir u√ßu≈ü teklifi (offer) √ºzerinde d√∂n
        for (const offer of flightOffers) {
          console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> --- YENƒ∞ TEKLƒ∞F ƒ∞≈ûLENƒ∞YOR (Ham Veri) ---", JSON.stringify(offer, null, 2));

          let price = null;
          let currency = null;

          // 7. Fiyat bilgilerini g√ºvenli bir ≈üekilde al
          if (offer.price && (typeof offer.price.grandTotal !== 'undefined' || typeof offer.price.total !== 'undefined')) {
            price = parseFloat(offer.price.grandTotal || offer.price.total);
            currency = offer.price.currency;
            console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Fiyat ve Para Birimi Alƒ±ndƒ±:", price, currency);
          } else {
            console.warn("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Uyarƒ±: Bu teklifte ge√ßerli fiyat (.price.grandTotal veya .price.total) bilgisi bulunamadƒ±. Atlanƒ±yor.", offer.price ? JSON.stringify(offer.price, null, 2) : "offer.price tanƒ±msƒ±z");
            continue; 
          }

          if (isNaN(price) || price === null) {
            console.warn("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Uyarƒ±: Ge√ßersiz fiyat deƒüeri (NaN veya null). Atlanƒ±yor.", price);
            continue;
          }

          // 8. Havayolu bilgisini g√ºvenli bir ≈üekilde al
          const airline = (offer.validatingAirlineCodes && offer.validatingAirlineCodes.length > 0) ? offer.validatingAirlineCodes[0] : "N/A";
          console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Havayolu Alƒ±ndƒ±:", airline);

          // 9. G√ºzergah ve segment bilgilerini g√ºvenli bir ≈üekilde al
          let departure, arrival, ucusTarihi;
          if (offer.itineraries && Array.isArray(offer.itineraries) && offer.itineraries.length > 0 &&
              offer.itineraries[0].segments && Array.isArray(offer.itineraries[0].segments) && offer.itineraries[0].segments.length > 0) {
            const itinerary = offer.itineraries[0];
            const firstSegment = itinerary.segments[0];
            const lastSegment = itinerary.segments[itinerary.segments.length - 1];

            if (firstSegment.departure && lastSegment.arrival &&
                firstSegment.departure.iataCode && firstSegment.departure.at &&
                lastSegment.arrival.iataCode && lastSegment.arrival.at) {
              departure = firstSegment.departure;
              arrival = lastSegment.arrival;
              ucusTarihi = departure.at.slice(0, 10); 
              console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Kalkƒ±≈ü, Varƒ±≈ü, U√ßu≈ü Tarihi Alƒ±ndƒ±:", JSON.stringify(departure, null, 2), JSON.stringify(arrival, null, 2), ucusTarihi);
            } else {
              console.warn("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Uyarƒ±: ƒ∞lk veya son segmentte kalkƒ±≈ü/varƒ±≈ü detaylarƒ± (iataCode, at) eksik. Atlanƒ±yor.", JSON.stringify(itinerary.segments, null, 2));
              continue;
            }
          } else {
            console.warn("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Uyarƒ±: Bu teklifte ge√ßerli g√ºzergah/segment bilgisi bulunamadƒ± (itineraries veya segments dizisi eksik/bo≈ü). Atlanƒ±yor.", offer.itineraries ? JSON.stringify(offer.itineraries, null, 2) : "offer.itineraries tanƒ±msƒ±z");
            continue;
          }

          // 10. ƒ∞≈ülenmi≈ü u√ßu≈ü objesini olu≈ütur
          const currentFlight = {
            fiyat: price,
            para_birimi: currency,
            havayolu: airline,
            kalkis_havaalani: departure.iataCode,
            varis_havaalani: arrival.iataCode,
            kalkis_saati_utc: departure.at,
            varis_saati_utc: arrival.at,
            ucus_tarihi: ucusTarihi,
            eklenme_timestamp_utc: new Date().toISOString()
          };
          processedFlights.push(currentFlight);
          console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> ƒ∞≈ülenmi≈ü U√ßu≈ü Eklendi:", JSON.stringify(currentFlight, null, 2));

          // 11. En ucuz u√ßu≈üu takip et
          if (enUcuzUcus === null || currentFlight.fiyat < enUcuzUcus.fiyat) {
            enUcuzUcus = currentFlight;
            console.log("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Yeni En Ucuz U√ßu≈ü Bulundu:", JSON.stringify(enUcuzUcus, null, 2));
          }
        } 
      } else {
