      console.error("[Node: U√ßu≈ü Verilerini ƒ∞≈üle] >> Hatalƒ± veri (jsonString):", jsonString ? jsonString.slice(0, 500) + "..." : "jsonString tanƒ±msƒ±z");
    }
  } else {
